import React, { useState } from 'react';

const CreateBlog = ({ createNewBlog }) => {
  const [newBlog, setNewBlog] = useState({
    title: '',
    author: '',
    url: '',
  });

  const createBlog = (event) => {
    event.preventDefault();
    createNewBlog(newBlog);

    setNewBlog({
      title: '',
      author: '',
      url: '',
    });
  };

  return (
    <form onSubmit={createBlog}>
      <div className='blogDiv'>
        title:
        <input
          id='title'
          value={newBlog.title}
          text='blogTitle'
          onChange={({ target }) =>
            setNewBlog({
              ...newBlog,
              title: target.value,
            })
          }
        />
      </div>
      <div>
        author:
        <input
          id='author'
          value={newBlog.author}
          text='blogAuthor'
          onChange={({ target }) =>
            setNewBlog({
              ...newBlog,
              author: target.value,
            })
          }
        />
      </div>
      <div>
        url:
        <input
          id='url'
          value={newBlog.url}
          text='blogUrl'
          onChange={({ target }) =>
            setNewBlog({
              ...newBlog,
              url: target.value,
            })
          }
        />
      </div>
      <button data-cy='createblog-submit' type='submit'>create</button>
    </form>
  );
};

export default CreateBlog;
