import React, { useState } from 'react';

const Blog = ({ blog }) => {
  const [view, setView] = useState(false);

  const toggleView = () => {
    setView(!view);
  };

  return (
    <div>
      <div>
        {blog.title} {blog.author}
        {view === false ? (
          <button onClick={toggleView}>view</button>
        ) : (
          <div>
            <button onClick={toggleView}>hide</button>
            <p>{blog.url}</p>
            <p>likes {blog.likes}</p>
            <p>{blog.user.name}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Blog;
